import { Build } from "@stencil/core";
export const hasSlot = (element, slotName) => {
    return Build.isServer
        ? Array.from(element.children).filter(el => el.matches(`[slot='${slotName}']`)).length > 0
        : !!element.querySelector(`:scope > [slot='${slotName}']`);
};
