/*!
 * Built with Duet Design System
 */
import { createPage } from "../../utils/test-utils";
describe("duet-callout", () => {
    it("should render a callout", async () => {
        const page = await createPage(`<duet-callout></duet-callout>`);
        const component = await page.find("duet-callout");
        expect(component).not.toBeNull();
    });
    it("should provide a property that switches the theme", async () => {
        const page = await createPage(`<duet-callout theme="turva"></duet-callout>`);
        const element = await page.find("duet-callout >>> .duet-callout");
        expect(element).toHaveClass("duet-theme-turva");
    });
    it("should provide a property that removes the margin of the component", async () => {
        const page = await createPage(`<duet-callout margin="none"></duet-callout>`);
        const element = await page.find("duet-callout >>> .duet-m-0");
        expect(element).not.toBeNull();
    });
});
