/*!
 * Built with Duet Design System
 */
import { createPage } from "../../utils/test-utils";
describe("duet-caption", () => {
    it("should render a caption", async () => {
        const page = await createPage(`<duet-caption></duet-caption>`);
        const component = await page.find("duet-caption");
        expect(component).not.toBeNull();
    });
    it("should provide a property that switches the theme", async () => {
        const page = await createPage(`<duet-caption theme="turva"></duet-caption>`);
        const element = await page.find("duet-caption");
        expect(element).toHaveClass("duet-theme-turva");
    });
    it("should provide a property that removes the margin of the component", async () => {
        const page = await createPage(`<duet-caption margin="none"></duet-caption>`);
        const element = await page.find(".duet-m-0");
        expect(element).not.toBeNull();
    });
    it("should provide a property that changes the size of caption", async () => {
        const page = await createPage(`<duet-caption size="small"></duet-caption>`);
        const element = await page.find("duet-caption");
        expect(element).toHaveClass("duet-caption-small");
    });
});
