/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-card", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-card heading="Analytics"> Card with a heading </duet-card>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
    it("should render an accessible component with image", async () => {
        const result = await testWithAxe(`
        <duet-card image="https://cdn.duetds.com/api/assets/illustrations/placeholder-dark.svg">
          <duet-heading level="h3">Card heading</duet-heading>
          <duet-paragraph margin="none">
            Card content lorem ipsum dolor sit amet consectetuer adipiscing elit no nummy laoreet lorem ipsum.
          </duet-paragraph>
          <duet-spacer></duet-spacer>
          <duet-button fixed url="#" padding="none" variation="plain" icon="action-arrow-right-small" icon-size="small" icon-right>
            Read more
          </duet-button>
        </duet-card>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
