/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-chip", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-chip variation="button" name="action" value="add">add to favorites</duet-chip>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
    it("should render an accessible component with a dropdown", async () => {
        const result = await testWithAxe(`
        <duet-chip variation="filter" popup="category-popup">Category</duet-chip>
        <duet-popup-menu id="category-popup">
          <duet-popup-menu-item value="health-value-id-68792">Health</duet-popup-menu-item>
          <duet-popup-menu-item>Pets</duet-popup-menu-item>
          <duet-popup-menu-item>Vehicles</duet-popup-menu-item>
        </duet-popup-menu>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
