/*!
 * Built with Duet Design System
 */
import { createPage } from "../../utils/test-utils";
describe("duet-chip", () => {
    it("should render a checkbox", async () => {
        const page = await createPage(`<duet-chip></duet-chip>`);
        const component = await page.find("duet-chip");
        expect(component).not.toBeNull();
    });
    it("should provide a property that renders aria-label", async () => {
        const page = await createPage(`<duet-chip accessible-label="test">chip</duet-chip>`);
        const element = await page.find("duet-chip >>> .duet-chip-container [aria-label]");
        expect(element.getAttribute("aria-label")).toEqual("test");
    });
    it("should render a checkbox input with variation 'filter'", async () => {
        const page = await createPage(`<duet-chip variation="filter">chip</duet-chip>`);
        const element = await page.find(`duet-chip >>> input[type="checkbox"]`);
        expect(element).not.toBeNull();
    });
    it("should render a check-mark icon with variation 'filter'", async () => {
        const page = await createPage(`<duet-chip variation="filter">chip</duet-chip>`);
        const element = await page.find(`duet-chip >>> duet-icon.check-mark`);
        expect(element).not.toBeNull();
    });
    it("should render an icon in a filter chip when provided", async () => {
        const page = await createPage(`<duet-chip variation="filter" icon="category-car">chip</duet-chip>`);
        const element = await page.find(`duet-chip >>> duet-icon:not(.check-mark)`);
        expect(element).not.toBeNull();
    });
    it("should render a button with variation 'button'", async () => {
        const page = await createPage(`<duet-chip variation="button">chip</duet-chip>`);
        const element = await page.find(`duet-chip >>> button`);
        expect(element).not.toBeNull();
    });
    it("should render a radio input with variation 'radio'", async () => {
        const page = await createPage(`<duet-chip variation="radio">chip</duet-chip>`);
        const element = await page.find(`duet-chip >>> input[type="radio"]`);
        expect(element).not.toBeNull();
    });
    it("should render an icon in a button chip when provided", async () => {
        const page = await createPage(`<duet-chip variation="button" icon="category-car">chip</duet-chip>`);
        const element = await page.find(`duet-chip >>> duet-icon`);
        expect(element).not.toBeNull();
    });
});
