import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-choice-group", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-choice-group value="one" label="Vakuutus" direction="horizontal" name="group" responsive>
          <duet-choice label="ElÃ¤invakuutus" type="radio" value="one" expand></duet-choice>
          <duet-choice label="Autovakuutus" type="radio" value="two" expand></duet-choice>
          <duet-choice label="Kotivakuutus" type="radio" value="three" expand></duet-choice>
        </duet-choice-group>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
