/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-collapsible", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-collapsible heading="Click to open details">
          Lorem ipsum dolor sit, amet consectetur adipisicing elit. Aperiam impedit, possimus minima repudiandae.
        </duet-collapsible>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
    it("should render an accessible component which is initially open", async () => {
        const result = await testWithAxe(`
        <duet-collapsible heading="Click to open details" open>
          Lorem ipsum dolor sit, amet consectetur adipisicing elit.
        </duet-collapsible>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
