import { getAllFixturesFiles } from "../../utils/fixture-utils";
import { createPage } from "../../utils/test-utils";
const ressources = getAllFixturesFiles(__dirname);
describe("duet-editable-table", () => {
    it("should render an duet-editable-table component", async () => {
        const page = await createPage(ressources.fixtures.table_with_actions_and_sorting);
        const component = await page.find("duet-editable-table");
        const items = await page.evaluate(() => {
            function mapToObj(inputMap) {
                const obj = {};
                inputMap.forEach(function (value, key) {
                    obj[key] = value;
                });
                return obj;
            }
            const element = document.querySelector("duet-editable-table");
            const items = element.rows;
            // the map is not serializable (natively) so we need to pass it along as an object
            return mapToObj(items);
        });
        expect(component).not.toBeNull();
        expect(component).toHaveClass("hydrated");
        //expect items to match fixture items
        expect(Object.keys(items).length).toBe(10);
    });
});
