import { createPage } from "../../utils/test-utils";
describe("duet-empty-state", () => {
    it("should render an empty state", async () => {
        const page = await createPage("<duet-empty-state></duet-empty-state>");
        const component = await page.find("duet-empty-state");
        expect(component).not.toBeNull();
    });
    it("should provide a property that changes the theme", async () => {
        const page = await createPage("<duet-empty-state theme='turva'></duet-empty-state>");
        const element = await page.find("duet-empty-state >>> .duet-empty-state");
        expect(element).toHaveClass("duet-theme-turva");
    });
});
