/*!
 * Built with Duet Design System
 */
import { createPage } from "../../utils/test-utils";
describe("duet-grid", () => {
    it("should render a grid", async () => {
        const page = await createPage(`<duet-grid></duet-grid>`);
        const component = await page.find("duet-grid");
        expect(component).not.toBeNull();
        expect(component).toHaveClass("hydrated");
    });
    it("should provide a property that enables the responsive behaviour", async () => {
        const page = await createPage(`<duet-grid responsive></duet-grid>`);
        const element = await page.find("duet-grid");
        expect(element).not.toHaveClass("duet-no-stacking");
    });
    it("should provide a property that disabled the responsive behaviour", async () => {
        const page = await createPage(`<duet-grid responsive="false"></duet-grid>`);
        const element = await page.find("duet-grid");
        expect(element).toHaveClass("duet-no-stacking");
    });
    it("should provide a property that changes the direction of the items", async () => {
        const page = await createPage(`<duet-grid direction="vertical"></duet-grid>`);
        const element = await page.find("duet-grid");
        expect(element).toHaveClass("duet-grid-direction-vertical");
    });
    it("should provide a property that changes the alignment of the items", async () => {
        const page = await createPage(`<duet-grid alignment="baseline"></duet-grid>`);
        const element = await page.find("duet-grid");
        expect(element).toHaveClass("duet-grid-align-baseline");
    });
    it("should provide a property that changes the distribution of the items", async () => {
        const page = await createPage(`<duet-grid distribution="center"></duet-grid>`);
        const element = await page.find("duet-grid");
        expect(element).toHaveClass("duet-grid-distribution-center");
    });
    it("should provide a property that adjusts aligment on mobile only", async () => {
        const page = await createPage(`<duet-grid mobile="center"></duet-grid>`);
        const element = await page.find("duet-grid");
        expect(element).toHaveClass("duet-grid-mobile-center");
    });
});
