/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-header", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-header
          language="fi"
          region="Pääkaupunkiseutu"
          contact="Ota yhteyttä"
          skip-to-id="#content"
          current-href="/"
          items='[
                    { "label": "Etusivu","href": "/" },
                    { "label": "Vakuutukset", "href": "#content" },
                    { "label": "Vahinkoasiat", "href": "#content" },
                    { "label": "Säästöt ja sijoitukset", "href": "#content" },
                    { "label": "Laskut", "href": "#content", "badge": true },
                    { "label": "Viestit", "href": "#content" }
                  ]'
          language-items='[
                    { "label": "Suomeksi", "country": "fi", "href": "/?lang=fi" },
                    { "label": "På Svenska", "country": "sv", "href": "/?lang=sv" },
                    { "label": "In English", "country": "en", "href": "/?lang=en" }
                  ]'
          contact-items='[
                    { "label": "Lähetä viesti", "href": "/viestit/laheta" },
                    { "label": "Avaa chat", "href": "/chat/" },
                    { "label": "Yhteystiedot", "href": "/yhteystiedot/", "external": true }
                  ]'
          session='{ "label": "Kirjaudu ulos", "href": "/?logout", "type": "logout" }'
          user='{ "label": "Elina", "href": "/?userId=elina" }'
        >
        </duet-header>
        <div id="content"></div>

      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
