/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-link", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
       <duet-link url="https://www.duetds.com">Link text</duet-link>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
    it("should render an accessible component in external variant", async () => {
        const result = await testWithAxe(`
       <duet-link url="https://www.duetds.com" external>Link that opens a new window</duet-link>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
    it("should render an accessible component in block variant", async () => {
        const result = await testWithAxe(`
        <duet-link
          url="#"
          icon="action-buy-insurance"
          icon-responsive
          icon-color="primary"
          variation="block"
          url="https://www.duetds.com"
        >
          <duet-heading margin="none" level="h2" visual-level="h4">Lorem ipsum</duet-heading>
          <duet-paragraph margin="none" class="duet-font-weight-normal duet-font-size-medium">
            Dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt.
          </duet-paragraph>
        </duet-link>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
