import { formatViolations, mergeRules, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
const disabledRules = {
    // It is impossible for dt and dd to be direct children of dl,
    // since the list elements are webcomponents with their own wrappers
    "definition-list": { enabled: false },
    // It is impossible for dt and dd to be direct children of dl,
    // since the list elements are webcomponents with their own wrappers
    dlitem: { enabled: false },
};
describe("[a11y] duet-list", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-list>
          <duet-list-item>
            <span slot="label">Nimi</span>
            <span slot="value">Lasse MÃ¤kinen</span>
          </duet-list-item>
          <duet-list-item>
            <span slot="label">Osoite</span>
            <span slot="value">Kotikatu 23 A 4, 00220 Helsinki, Suomi</span>
          </duet-list-item>
        </duet-list>
      `, mergeRules(axeRunOptions, disabledRules));
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
