/*!
 * Built with Duet Design System
 */
import { createPage } from "../../utils/test-utils";
describe("duet-logo", () => {
    it("should render a logo", async () => {
        const page = await createPage(`<duet-logo></duet-logo>`);
        const component = await page.find("duet-logo");
        expect(component).not.toBeNull();
        expect(component).toHaveClass("hydrated");
    });
    it("should provide a property that switches the theme", async () => {
        const page = await createPage(`<duet-logo theme="turva"></duet-logo>`);
        const element = await page.find("duet-logo >>> .duet-logo");
        expect(element).toHaveClass("duet-theme-turva");
    });
    it("should provide a property that removes the margin of the component", async () => {
        const page = await createPage(`<duet-logo margin="none"></duet-logo>`);
        const element = await page.find(".duet-m-0");
        expect(element).not.toBeNull();
    });
    it("should provide a property that changes the size of the logo", async () => {
        const page = await createPage(`<duet-logo size="large"></duet-logo>`);
        const element = await page.find("duet-logo >>> .duet-logo");
        expect(element).toHaveClass("large");
    });
    it("should provide a property that changes logo’s href", async () => {
        const page = await createPage(`<duet-logo href="demo"></duet-logo>`);
        const element = await page.find("duet-logo >>> .duet-logo");
        expect(element.getAttribute("href")).toEqual("demo");
    });
    it("should provide a property that changes logo’s language", async () => {
        const page = await createPage(`<duet-logo language="en"></duet-logo>`);
        const element = await page.find("duet-logo >>> .duet-logo svg title");
        expect(element.textContent).toEqual("LocalTapiola");
    });
    it("should provide a property that changes logo to inverse mode", async () => {
        const page = await createPage(`<duet-logo inverse></duet-logo>`);
        const element = await page.find("duet-logo >>> .duet-logo svg");
        expect(element.getAttribute("fill")).toEqual("rgb(255, 255, 255)");
    });
});
