/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-notification", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <div role="list">
          <duet-notification highlight url="https://www.duetds.com" date="23.11.2020">
            <duet-heading level="h2" visual-level="h6"> Uusi vakuutus </duet-heading>
            <duet-paragraph size="small" margin="none">
              Uuden kotivakuutuksesi tiedot ja asiakirjat löydät klikkaamalla tästä.
            </duet-paragraph>
          </duet-notification>
        </div>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
