/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-radio-group", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-radio-group label="Choose" value="one" name="group">
          <duet-radio value="one" label="Option one"></duet-radio>
          <duet-radio value="two" label="Option two"></duet-radio>
          <duet-radio value="three" label="Option three"></duet-radio>
        </duet-radio-group>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
    it("should render an accessible component with label, tooltip and caption", async () => {
        const result = await testWithAxe(`
        <duet-radio-group
          responsive
          label="This choice group has a tooltip"
          tooltip="Hello, Iâ€™m a tooltip! To close me, you can click outside of the tooltip, hit ESC key or click the X button."
          caption="This is a caption lorem ipsum dolor sit amet consectetuer adipiscing elit."
          direction="horizontal"
          value="one"
          name="group2"
        >
          <duet-radio value="one" label="Option one"></duet-radio>
          <duet-radio value="two" label="Option two"></duet-radio>
          <duet-radio value="three" label="Option three"></duet-radio>
        </duet-radio-group>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
