import { DuetRangeStepper } from "./duet-range-stepper";
describe("duet-range-stepper", () => {
    it("should not allow non-integer stepIndex when growing stepSize beyond last page", () => {
        const component = new DuetRangeStepper();
        component.stepSize = 10;
        component.total = 33;
        component.stepIndex = 3;
        expect(component.stepIndex).toEqual(3);
        component.watchStepSizeStateHandler(30, 10);
        expect(component.stepIndex).toEqual(2);
    });
    it("should clamp initially out of bounds stepIndex to last page", () => {
        const component = new DuetRangeStepper();
        component.stepSize = 10;
        component.total = 33;
        component.stepIndex = 5;
        component.componentWillLoad();
        expect(component.stepIndex).toEqual(4);
    });
});
