/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-scrollable", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
       <duet-scrollable>
          <button class="duet-dummy-placeholder" aria-label="Click to open image 1">1</button>
          <button class="duet-dummy-placeholder" aria-label="Click to open image 2">2</button>
          <button class="duet-dummy-placeholder" aria-label="Click to open image 3">3</button>
        </duet-scrollable>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
