/*!
 * Built with Duet Design System
 */
import { createPage } from "../../utils/test-utils";
describe("duet-layout", () => {
    it("should render a section layout", async () => {
        const page = await createPage(`<duet-section-layout><div>content</div></duet-section-layout>`);
        const component = await page.find("duet-section-layout");
        expect(component).not.toBeNull();
        expect(component).toHaveClass("hydrated");
    });
    it("should provide a property that removes the margin of the component", async () => {
        const page = await createPage(`<duet-section-layout margin="none"><div>content</div></duet-section-layout>`);
        const element = await page.find("duet-section-layout >>> .duet-m-0");
        expect(element).not.toBeNull();
    });
    it("should provide a property that middle aligns the layout", async () => {
        const page = await createPage(`<duet-section-layout middle><div>content</div></duet-section-layout>`);
        const element = await page.find("duet-section-layout");
        expect(element).toHaveClass("duet-middle");
    });
});
