/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-select", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-select
          label="Kaupunki"
          placeholder="Valitse"
          items='[
            { "label": "Helsinki", "value": "1" },
            { "label": "Tampere", "value": "2" },
            { "label": "Vantaa", "value": "3" },
            { "label": "Espoo", "value": "4" }
          ]'
        >
        </duet-select>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
    it("should render an accessible component with grouped options", async () => {
        const result = await testWithAxe(`
        <duet-select
          label="Kaupunki"
          placeholder="Valitse"
          items='[
            { label: "Helsinki", value: "1" },
            {
              label: "Grouped options",
              options: [
                { label: "Tampere", value: "2" },
                { label: "Vantaa", value: "3" },
              ],
            },
            { label: "Espoo", value: "4" },
          ]'
        >
        </duet-select>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
