/*!
 * Built with Duet Design System
 */
import { createPage } from "../../utils/test-utils";
describe("duet-spacer", () => {
    it("should render a spacer", async () => {
        const page = await createPage("<duet-spacer></duet-spacer>");
        const component = await page.find("duet-spacer");
        expect(component).not.toBeNull();
        expect(component).toHaveClass("hydrated");
    });
    it("should provide a property that changes spacing", async () => {
        const page = await createPage(`<duet-spacer size="large"></duet-spacer>`);
        const element = await page.find("duet-spacer");
        expect(element).toHaveClass("large");
    });
    it("should provide a property that changes direction", async () => {
        const page = await createPage(`<duet-spacer direction="horizontal"></duet-spacer>`);
        const element = await page.find("duet-spacer");
        expect(element).toHaveClass("duet-spacer-horizontal");
    });
    it("should provide a property that changes responsive breakpoint", async () => {
        const page = await createPage(`<duet-spacer breakpoint="x-small"></duet-spacer>`);
        const element = await page.find("duet-spacer");
        expect(element).toHaveClass("duet-spacer-x-small");
    });
});
