/*!
 * Built with Duet Design System
 */
import { createPage } from "../../utils/test-utils";
describe("duet-spinner", () => {
    it("should render a spinner", async () => {
        const page = await createPage(`<duet-spinner></duet-spinner>`);
        const component = await page.find("duet-spinner");
        expect(component).not.toBeNull();
        expect(component).toHaveClass("hydrated");
    });
    it("should provide a property that renders aria-label", async () => {
        const page = await createPage(`<duet-spinner accessible-label="test"></duet-spinner>`);
        const element = await page.find("duet-spinner >>> .duet-spinner");
        expect(element.getAttribute("aria-label")).toEqual("test");
    });
    it("should provide a property that renders spinner in different sizes", async () => {
        const page = await createPage(`<duet-spinner size="large"></duet-spinner>`);
        const element = await page.find("duet-spinner >>> .duet-spinner");
        expect(element).toHaveClass("large");
    });
    it("should provide a property that renders spinner in different colors", async () => {
        const page = await createPage(`<duet-spinner color="color-primary"></duet-spinner>`);
        const color = await page.evaluate(() => {
            const spinner = document.querySelector("duet-spinner").shadowRoot.querySelector(".duet-spinner");
            return window.getComputedStyle(spinner).color;
        });
        expect(color).toEqual("rgb(0, 119, 179)");
    });
});
