/*!
 * Built with Duet Design System
 */
import { formatViolations, mergeRules, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
const disabledRules = {
    // Axe complains about using aria-selected which is allowed only for certain roles.
    // Deep refactor would be needed to fulfill this requirement
    "aria-allowed-attr": { enabled: false },
};
describe("[a11y] duet-stepper", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
        <duet-stepper>
          <duet-step heading="Your name">
              <duet-button margin="none" variation="primary" class="next">Next</duet-button>
          </duet-step>

          <duet-step heading="Final step">
              <duet-button margin="none" variation="primary" class="next">Finish</duet-button>
          </duet-step>
        </duet-stepper>
      `, mergeRules(axeRunOptions, disabledRules));
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
