import { createPage } from "../../utils/test-utils";
describe("duet-tab", () => {
    it("should render a tab", async () => {
        const page = await createPage("<duet-tab></duet-tab>");
        const component = await page.find("duet-tab");
        expect(component).not.toBeNull();
    });
    it("should provide a property that makes the tab selected", async () => {
        const page = await createPage(`<duet-tab selected></duet-tab>`);
        const element = await page.find("duet-tab .duet-tab");
        expect(element).toHaveClass("selected");
    });
});
