/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-textarea", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
       <duet-textarea expand label="Textarea label" placeholder="Placeholder text"> </duet-textarea>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
    it("should render an accessible component with input characters counter", async () => {
        const result = await testWithAxe(`
          <duet-textarea
            expand
            label="Textarea label"
            placeholder="Placeholder text"
            maxlength="100"
            counter-label="Merkkejä jäljellä"
            counter
          >
          </duet-textarea>
          `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
