/*!
 * Built with Duet Design System
 */
import { formatViolations, options as axeRunOptions, testWithAxe } from "../../utils/axe-core-utils";
describe("[a11y] duet-tray", () => {
    it("should render an accessible component", async () => {
        const result = await testWithAxe(`
       <duet-tray responsive="false" active>
        <duet-grid alignment="center">
          <duet-grid-item margin="none" fill>
            <duet-paragraph size="small" margin="none">Hinta yhteensä</duet-paragraph>
          </duet-grid-item>
          <duet-grid-item margin="none">
            <span class="duet-font-size-m duet-font-weight-semi-bold">189,90&nbsp;€</span>
          </duet-grid-item>
        </duet-grid>
      </duet-tray>
      `, axeRunOptions);
        expect(result).not.toBeNull();
        expect(formatViolations(result.violations)).toEqual([]);
    });
});
