import { validateTotalSizeIsAboveMax } from "./upload-validators";
describe("validateTotalSizeIsAboveMax", () => {
    it("file list size is above max", () => {
        const fileList = [
            { valid: true, size: 100 },
            { valid: true, size: 200 },
            { valid: true, size: 300 },
        ];
        expect(validateTotalSizeIsAboveMax(fileList, 599)).toBe(true);
    });
    it("file list size is same as max", () => {
        const fileList = [
            { valid: true, size: 100 },
            { valid: true, size: 200 },
            { valid: true, size: 300 },
        ];
        expect(validateTotalSizeIsAboveMax(fileList, 600)).toBe(true);
        expect(validateTotalSizeIsAboveMax(fileList, 601)).toBe(false);
    });
    it("file list size is lower than max", () => {
        const fileList = [
            { valid: true, size: 100 },
            { valid: true, size: 200 },
            { valid: true, size: 300 },
        ];
        expect(validateTotalSizeIsAboveMax(fileList, 599)).toBe(true);
        expect(validateTotalSizeIsAboveMax(fileList, 601)).toBe(false);
    });
    it("file list size is calculated based on valid files", () => {
        const fileList = [
            { valid: true, size: 100 },
            { valid: false, size: 200 },
            { valid: true, size: 300 },
        ];
        expect(validateTotalSizeIsAboveMax(fileList, 599)).toBe(false);
        expect(validateTotalSizeIsAboveMax(fileList, 399)).toBe(true);
    });
});
