import { createPage } from "../../utils/test-utils";
describe("duet-visually-hidden", () => {
    it("should render a visually hidden element", async () => {
        const page = await createPage(`<duet-visually-hidden></duet-visually-hidden>`);
        const component = await page.find("duet-visually-hidden");
        expect(component).not.toBeNull();
        expect(component).toHaveClass("hydrated");
    });
});
