import { debounce } from "./js-utils";
// Tell Jest to mock all timeout functions
jest.useFakeTimers();
describe("debounce", () => {
    let func;
    let debouncedFunc;
    beforeEach(() => {
        func = jest.fn();
        debouncedFunc = debounce(func, 1000);
    });
    it("executes only once", () => {
        for (let i = 0; i < 100; i++) {
            debouncedFunc();
        }
        // Fast-forward time
        jest.runAllTimers();
        expect(func).toBeCalledTimes(1);
    });
});
