/*!
 * Built with Duet Design System
 */
import { h } from "@stencil/core";
export const DuetPicture = ({ img, sources }) => {
    return (h("picture", null, sources &&
        sources.map(source => (h("source", { srcSet: source.srcset, media: source.media, type: source.type, sizes: source.sizes }))), h("img", Object.assign({}, img))));
};
